import { useState } from "react";
import styled from "styled-components";

export default function MediaTypeSelection() {
  const [mediaSelected, setMediaSelected] = useState("");
  const handleChange = (event) => {
    setMediaSelected(event.target.value);
  };

  const mediaTypeOptions = [
    { value: "", text: "Filmes / Séries", disabled: "disabled selected" },
    { value: "films", text: "Filmes" },
    { value: "tvshows", text: "Séries" },
  ];

  return (
    <SelectionWrapper>
      <select value={mediaTypeOptions} onChange={handleChange}>
        {mediaTypeOptions.map((option) => (
          <option
            key={option.value}
            value={option.value}
            disabled={option.disabled}
          >
            {" "}
            {option.text}
          </option>
        ))}
      </select>
    </SelectionWrapper>
  );
}

const SelectionWrapper = styled.div`
  width: 50%;
  margin-left: 50vw;
  position: fixed;
  z-index: 3;
  top: 240px;
  display: ${(props) => (props.isWishListClicked ? "none" : "inherit")};
  select {
    height: 40px;
    background-color: #f5f5f5;
    border: 0px;
    font-size: 16px;
    font-weight: 600;
  }

  select:focus {
    outline: none;
  }
`;
