import styled from "styled-components";
import ReactStars from "react-rating-stars-component";
import { useState } from "react";
import { DebounceInput } from "react-debounce-input";
import useToken from "../hooks/useToken";
import { updateGradeOrReviewOrDate } from "../services/services";
import { toast } from "react-toastify";

export default function BookBox({ book }) {
  const token = useToken();
  const ratingChanged = async (newRating) => {
    try {
      await updateGradeOrReviewOrDate(token, book.book_api_id, {
        newGrade: newRating,
      });
      toast("Nota atualizada com sucesso");
    } catch (error) {
      console.log(error);
    }
  };
  console.log(book);
  const [isDescriptionOpen, setIsDescriptionOpen] = useState(false);

  return (
    <Wrapper isDescriptionOpen={isDescriptionOpen}>
      <BookPicture src={book.img} alt={`${book.title} poster`} />
      <BookDescription>
        <h1>{book.title}</h1>
        <h2>{book.author}</h2>
      </BookDescription>
      <RatingWrapper>
        <BookDescription>
          <h3>
            <span>Lido em:</span> <br />
            <input
              type="date"
              value={book.read_at.slice(0, 10)}
              min="2000-01-02"
            ></input>
          </h3>
        </BookDescription>

        <ReactStars
          value={Number(book.grade)}
          count={5}
          onChange={ratingChanged}
          size={24}
          isHalf={true}
          emptyIcon={<i className="far fa-star"></i>}
          halfIcon={<i className="fa fa-star-half-alt"></i>}
          fullIcon={<i className="fa fa-star"></i>}
          activeColor="#ffb703"
        />
      </RatingWrapper>
      <OpenMenuWrapper onClick={() => setIsDescriptionOpen(!isDescriptionOpen)}>
        {isDescriptionOpen ? "-" : "+"}
      </OpenMenuWrapper>
      {isDescriptionOpen ? (
        <ReviewWrapper>
          <h1>Resenha Pessoal:</h1>
          {/* <DebounceInput
            autoComplete="off"
            placeholder={`${book.review}`}
            type="text"
            name="search"
            value={"a"}
            onChange={(e) => {}}
          ></DebounceInput> */}
          <input type="text"></input>
        </ReviewWrapper>
      ) : (
        ""
      )}
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  justify-content: space-between;
  width: calc(100vw - 30px);
  margin: 0 15px 26px 15px;
  border-radius: 8px;
  height: ${(props) => (props.isDescriptionOpen ? "160px" : "100px")};
  background-color: #fcffdf;
  position: relative;
  transition-property: height;
  transition-duration: 2s;
  transition-timing-function: cubic-bezier(0.19, 1, 0.22, 1);
  font-family: "Lato", sans-serif;
  padding: 16px 12px 12px 12px;
`;

const BookPicture = styled.img`
  width: 44px !important;
  height: 68px !important;
  border-radius: 6px;
  object-fit: contain;
`;

const BookDescription = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  padding-left: 8px;
  max-width: 140px;
  max-height: 60px;
  h1 {
    font-weight: 700;
    font-size: 17px;
    margin-bottom: 8px;
  }
  h2,
  span {
    font-style: italic;
  }
  h3 {
    font-size: 15px;
    margin-bottom: 2px;
  }
`;

const RatingWrapper = styled.div`
  input {
    font-family: "Lato", sans-serif;
    background-color: #fcffdf;
    border: none;
    width: 110px;
  }
  textarea:focus,
  input:focus {
    box-shadow: 0 0 0 0;
    outline: 0;
  }
`;

const OpenMenuWrapper = styled.div`
  border-radius: 50%;
  width: 30px;
  height: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #38b7ff;
  position: absolute;
  bottom: -15px;
  left: calc(50% - 15px);
  font-family: "Barlow Condensed", sans-serif;
  font-size: 26px;
  padding-bottom: 6px;
  cursor: pointer;
`;

const ReviewWrapper = styled.div`
  position: absolute;
  bottom: 20px;
  max-width: 95%;
  max-height: 60px;

  input {
    background-color: #ffffff;
    height: 20px;
    width: 100%;
    border-radius: 8px;
    border: none;
    font-size: 19px;
    align-items: center;
    padding: 0 15px;
    margin-top: 12px;
    word-break: break-all;
    line-break: auto;
  }
`;
