import styled from "styled-components";

export default function TvShowWishListBox({ tvshow }) {
  return (
    <Wrapper>
      <TvShowDescription>
        <h1>{tvshow.title}</h1>
        <h2>{tvshow.creator}</h2>
        <h4>{tvshow.seasons_number} temporadas</h4>
      </TvShowDescription>{" "}
      <TvShowPicture src={tvshow.img} alt={`${tvshow.title} poster`} />{" "}
      <TvShowDescription>
        <h5>Sobre:</h5>
        <h3>{tvshow.tagline}</h3>
      </TvShowDescription>{" "}
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: calc(100vw - 30px);
  margin: 0 15px 26px 15px;
  border-radius: 8px;
  height: 120px;
  max-height: 200px;
  background-color: #fcffdf;
  position: relative;
  font-family: "Lato", sans-serif;
  padding: 10px;
`;

const TvShowPicture = styled.img`
  width: 60px !important;
  height: 84px !important;
  border-radius: 6px;
  margin-left: 6px;
  object-fit: contain;
`;

const TvShowDescription = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  padding-left: 8px;
  width: 120px;
  max-height: 60px;
  h1 {
    font-weight: 700;
    font-size: 17px;
    margin-bottom: 6px;
  }
  h2,
  span {
    font-style: italic;
  }
  h3 {
    font-size: 15px;
    max-width: 120px;
  }
  h4 {
    font-size: 13px;
    margin-top: 4px;
  }
  h5 {
    font-size: 14px;
    font-style: italic;
    margin-bottom: 4px;
  }
`;
