import styled from "styled-components";
import { DebounceInput } from "react-debounce-input";
import { FiSearch } from "react-icons/fi";
import { useEffect, useState } from "react";
import { getApiBooks } from "../services/services";
// https://www.fullperformance.com.br/images/evento/2020-03-01_1a_logo_default3.jpg   - imagem indisponível
export default function SearchBox() {
  const [search, setSearch] = useState("");
  const [booksList, setBooksList] = useState([]);

  useEffect(() => {
    if (search.length > 3) {
      const requisition = getApiBooks(search);
      requisition.then((res) => {
        const books = res.data.items;
        console.log(books);
        books.forEach((book, index) => {
          console.log(index);
        });
      });
    }
  }, [search]);

  return (
    <Wrapper>
      <BoxSearch>
        <DebounceInput
          autoComplete="off"
          placeholder="Procure pelo título"
          type="text"
          name="search"
          value={search}
          debounceTimeout={300}
          onChange={(e) => setSearch(e.target.value)}
        ></DebounceInput>
      </BoxSearch>
      <StyledSearch>
        <FiSearch />
      </StyledSearch>
      <ResultList></ResultList>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 95vw;
  position: relative;
`;
const BoxSearch = styled.form`
  input {
    background-color: #ffffff;
    height: 45px;
    width: calc(100vw - 30px);
    margin: 0 15px;
    border-radius: 8px;
    border: none;
    font-size: 19px;
    align-items: center;
    padding: 0 15px;
    margin-top: 12px;
    z-index: 1;

    @media (max-width: 850px) {
    }
  }

  input::placeholder {
    color: #c6c6c6;
  }
  textarea:focus,
  input:focus {
    box-shadow: 0 0 0 0;
    outline: 0;
  }
`;

const ResultList = styled.div`
  @media (max-width: 850px) {
    position: absolute;
    width: 100%;
    height: auto;
    margin-top: 58px;
  }
`;
const StyledSearch = styled.div`
  position: absolute;
  top: 22px;
  right: 8px;
  z-index: 2;
  color: #c6c6c6;
  font-size: 24px;
  cursor: pointer;
`;
