import { useContext, useEffect, useState } from "react";
import LoadingAnimation from "../../common/LoadingAnimation";
import { UserContext } from "../../contexts/userContext";
import {
  getApiTrendingFilms,
  getApiTrendingTvShows,
} from "../../services/external_api";
import styled from "styled-components";

export default function Trending() {
  const { bottomMenuSelected, mainMenuSelected } = useContext(UserContext);
  const [medias, setMedias] = useState([]);

  useEffect(() => {
    async function getMedias() {
      try {
        const newMedias = await getApiTrendingFilms("popular");
        setMedias(newMedias.results);
        console.log(medias);
      } catch (error) {
        console.log(error);
      }
    }
    getMedias();
  }, []);
  console.log(medias);
  if (mainMenuSelected === "POPULARES") {
    return (
      <Wrapper>{medias.length !== 0 ? "foi" : <LoadingAnimation />}</Wrapper>
    );
  } else if (mainMenuSelected === "PASSANDO HOJE") {
    return (
      <Wrapper>{medias.length !== 0 ? "foi" : <LoadingAnimation />}</Wrapper>
    );
  }
}

const Wrapper = styled.div`
  width: 100vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
`;
