import { useState } from "react";
import styled from "styled-components";

export default function MediaTypeSelection({
  mediaSelected,
  setMediaSelected,
}) {
  const handleChange = (event) => {
    setMediaSelected(event.target.value);
  };

  const mediaTypeOptions = [
    { value: "", text: "Filmes / Séries", disabled: "disabled" },
    { value: "films", text: "Filmes" },
    { value: "tvshows", text: "Séries" },
  ];

  return (
    <SelectionWrapper value={mediaSelected}>
      <select
        value={mediaSelected}
        onChange={handleChange}
        test={mediaSelected}
      >
        {mediaTypeOptions.map((option) => (
          <option
            key={option.value}
            value={option.value}
            disabled={option.disabled}
          >
            {option.text}
          </option>
        ))}
      </select>
    </SelectionWrapper>
  );
}

const SelectionWrapper = styled.div`
  select {
    height: 40px;
    width: ${(props) => (props.value === "" ? "auto" : "86px")};
    background-color: #f8edeb;
    border: 0px;
    font-size: 16px;
    font-weight: 600;
    position: fixed;
    z-index: 3;
    top: 190px;
    right: ${(props) =>
      props.value === "" ? "calc(100vw / 2 - 60px)" : "calc(100vw / 2 - 44px)"};
    right: ;
  }

  select:focus {
    outline: none;
  }
`;
