import styled from "styled-components";

export default function FilmWishListBox({ film }) {
  return (
    <Wrapper>
      <FilmDescription>
        <h1>{film.title}</h1>
        {/* <h2>{book.author}</h2> */}
      </FilmDescription>{" "}
      <FilmPicture src={film.img} alt={`${film.title} poster`} />{" "}
      <FilmDescription>
        <h3>
          <span>Sobre:</span> <br />
          {film.tagline}
        </h3>
      </FilmDescription>{" "}
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: calc(100vw - 30px);
  margin: 0 15px 26px 15px;
  border-radius: 8px;
  height: 120px;
  max-height: 200px;
  background-color: #fcffdf;
  position: relative;
  font-family: "Lato", sans-serif;
  padding: 10px;
`;

const FilmPicture = styled.img`
  width: 66px !important;
  height: 84px !important;
  border-radius: 6px;
  margin-left: 22px;
  object-fit: contain;
`;

const FilmDescription = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  padding-left: 8px;
  max-width: 140px;
  max-height: 60px;
  h1 {
    font-weight: 700;
    font-size: 17.5px;
    margin-bottom: 12px;
  }
  h2,
  span {
    font-style: italic;
  }
  h3 {
    font-size: 13px;
    margin-bottom: 2px;
    max-width: 120px;
  }
`;
