import styled from "styled-components";

export default function FilmWishListBox({ film }) {
  return (
    <Wrapper>
      <FilmDescription>
        <h1>{film.title}</h1>
        <h2>{film.runtime} minutos</h2>
        <h4>Nota m√©dia: {Number(film.vote_average).toFixed(1)}</h4>
      </FilmDescription>{" "}
      <FilmPicture src={film.img} alt={`${film.title} poster`} />{" "}
      <FilmDescription>
        <h5>Sobre:</h5> <h3>{film.tagline}</h3>
      </FilmDescription>{" "}
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: calc(100vw - 30px);
  margin: 0 15px 26px 15px;
  border-radius: 8px;
  height: 120px;
  max-height: 200px;
  background-color: #fcffdf;
  position: relative;
  font-family: "Lato", sans-serif;
  padding: 10px;
`;

const FilmPicture = styled.img`
  width: 60px !important;
  height: 86px !important;
  border-radius: 6px;
  object-fit: contain;
`;

const FilmDescription = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  padding-left: 8px;
  width: 120px;
  max-height: 60px;
  h1 {
    font-weight: 700;
    font-size: 17px;
    margin-bottom: 6px;
  }
  h2,
  span {
    font-style: italic;
  }
  h3 {
    font-size: 13px;
    max-width: 120px;
  }
  h4 {
    font-size: 13px;
    margin-top: 4px;
  }
  h5 {
    font-size: 16px;
    font-style: italic;
    margin-bottom: 4px;
  }
`;
