import styled from "styled-components";
import { toast } from "react-toastify";
import { useState } from "react";
import logo_img_acima from "../../assets/img/logo-img-acima.png";
import logo_img_acima2 from "../../assets/img/logo-img-acima2.png";
import logo_img_acima3 from "../../assets/img/logo-img-acima3.png";
import logo_img_acima4 from "../../assets/img/logo-img-acima4.png";
import logo_img_acima5 from "../../assets/img/logo-img-acima5.png";

export default function Enroll() {
  const [form, setForm] = useState({});
  function submitForm(event) {
    event.preventDefault();
  }

  function handleForm({ value, name }) {
    setForm({
      ...form,
      [name]: value,
    });
  }

  return (
    <Wrapper>
      <Logo src={logo_img_acima4} alt="Cine-book club logo" />
      <FormWrapper onSubmit={submitForm}>
        <input
          placeholder="E-mail"
          name="email"
          type="email"
          required
          onChange={(e) => {
            handleForm({ name: e.target.name, value: e.target.value });
          }}
        />
        <input
          placeholder="Senha"
          name="password"
          type="password"
          required
          onChange={(e) => {
            handleForm({ name: e.target.name, value: e.target.value });
          }}
        />
        <input
          placeholder="Repita sua senha"
          name="confirmed_password"
          type="password"
          required
          onChange={(e) => {
            handleForm({ name: e.target.name, value: e.target.value });
          }}
        />
      </FormWrapper>
      <Link>Já possui conta? Faça o login</Link>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  background-color: #ffd7ba;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
`;

const Logo = styled.img`
  max-width: 120vw;
`;

const FormWrapper = styled.form`
  display: flex;
  flex-direction: column;
  width: 80vw;

  input {
    height: 48px;
    margin-bottom: 12px;
    background-color: #ffe5d9;
    border: solid #e8e8e4;
    border-radius: 5px;
    padding-left: 12px;
    font-family: "Raleway", sans-serif;
    font-weight: 500;
    font-size: 18px;
    color: #000000;
    word-wrap: break-word;
  }

  input::placeholder {
    font-weight: 400;
    color: #495057;
  }

  input:focus {
    outline: none;
  }
`;

const Link = styled.p`
  margin-top: 6px;
  font-size: 18px;
  font-family: "Raleway", sans-serif;
  color: #495057;
`;
