import styled from "styled-components";
import { DebounceInput } from "react-debounce-input";
import { FiSearch } from "react-icons/fi";
import { useEffect, useState } from "react";
import { getApiBooks } from "../services/services";
import SearchedBook from "./SearchedBook";

const unavailableImg =
  "https://www.fullperformance.com.br/images/evento/2020-03-01_1a_logo_default3.jpg";
export default function SearchBox() {
  const [search, setSearch] = useState("");
  const [books, setBooks] = useState([]);
  const [booksList, setBooksList] = useState([]);
  const [render, setRender] = useState(0);

  useEffect(() => {
    if (search.length > 3) {
      const requisition = getApiBooks(search);
      requisition.then((res) => {
        setBooks(res.data.items);

        // async function fetchData() {
        //   const a = getApiBooks(search);
        //   a.then((res) => {
        //     console.log(res);
        //     return res.data.items;
        //   });
        // }
        // const b = fetchData();
        // console.log("apos");
        // setBooks(b);
        const newList = [];
        books.forEach((book) => {
          if (book.volumeInfo.authors && book.volumeInfo.description) {
            const bookUnity = {
              title: book.volumeInfo.title,
              author: book.volumeInfo.authors,
              img: book.volumeInfo.imageLinkgs
                ? book.volumeInfo.imageLinkgs.thumbnail
                : unavailableImg,
              description: book.volumeInfo.description,
            };
            newList.push(bookUnity);
          }
        });
        setBooksList(newList);
      });
    }
  }, [search]);
  console.log(booksList);
  return (
    <Wrapper>
      <BoxSearch>
        <DebounceInput
          autoComplete="off"
          placeholder="Procure pelo tÃ­tulo"
          type="text"
          name="search"
          value={search}
          debounceTimeout={300}
          onChange={(e) => setSearch(e.target.value)}
        ></DebounceInput>
      </BoxSearch>
      <StyledSearch>
        <FiSearch />
      </StyledSearch>
      <ResultList>
        {booksList.length !== 0
          ? booksList.map((book, index) => (
              <SearchedBook
                key={index}
                title={book.title}
                author={book.author}
                img={book.img}
                description={book.description}
              />
            ))
          : ""}
      </ResultList>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 95vw;
  position: relative;
`;
const BoxSearch = styled.form`
  input {
    background-color: #ffffff;
    height: 45px;
    width: calc(100vw - 30px);
    margin: 0 15px;
    border-radius: 8px;
    border: none;
    font-size: 19px;
    align-items: center;
    padding: 0 15px;
    margin-top: 12px;
    z-index: 1;

    @media (max-width: 850px) {
    }
  }

  input::placeholder {
    color: #c6c6c6;
  }
  textarea:focus,
  input:focus {
    box-shadow: 0 0 0 0;
    outline: 0;
  }
`;

const ResultList = styled.div`
  width: calc(100vw - 30px);
  margin: 10px 15px;
  height: auto;

  display: flex;
  flex-direction: column;
`;
const StyledSearch = styled.div`
  position: absolute;
  top: 22px;
  right: 8px;
  z-index: 2;
  color: #c6c6c6;
  font-size: 24px;
  cursor: pointer;
`;
