import styled from "styled-components";
import { useEffect, useState } from "react";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@material-ui/core";

export default function SearchedBook({ title, author, description, img }) {
  console.log(img, title);
  const [confirmDialog, setConfirmDialog] = useState({
    isOpen: false,
    message: `Quer adicionar ${title} ?`,
  });
  function acceptFunc() {
    console.log("accepted");
  }
  function rejectFunc() {
    console.log("rejected");
  }
  const confirm = (event) => {
    confirmDialog({
      trigger: event.currentTarget,
      message: "Are you sure you want to proceed?",
      header: "Confirmation",
      icon: "pi pi-exclamation-triangle",
      accept: () => acceptFunc(),
      reject: () => rejectFunc(),
    });
  };
  return (
    <Wrapper>
      <img src={img} alt="" />
      <h1>
        {" "}
        {title} - {author}{" "}
      </h1>
      <button onClick={confirm} icon="pi pi-check" label="Confirm"></button>
      <ConfirmDialog />
    </Wrapper>
  );
}

const Wrapper = styled.div`
  background-color: #ffffff;
  display: flex;
  align-items: center;
  padding: 10px;
  border-bottom-left-radius: 8px;
  border-bottom-right-radius: 8px;

  img {
    width: 39px !important;
    height: 52px !important;
    border-radius: 6px;
    object-fit: contain;
    cursor: pointer;
  }
  h1 {
    font-size: 19px;
    font-weight: 400;
    color: #515151;
    margin: 0px 10px;
    cursor: pointer;
    font-family: "Lato";
  }
`;
