import { useState } from "react";
import styled from "styled-components";

function MenuIcon({
  outlineIcon,
  fullIcon,
  name,
  menuClicked,
  setMenuClicked,
}) {
  const [isClicked, setIsClicked] = useState(false);

  function clickMenu() {
    if (menuClicked !== name) {
      setIsClicked(true);
      setMenuClicked(name);
    } else {
      setIsClicked(false);
      setMenuClicked("");
    }
  }

  return (
    <IconWrapper onClick={clickMenu}>
      <ion-icon
        name={!isClicked && menuClicked === name ? outlineIcon : fullIcon}
      ></ion-icon>
      <IconName>{name}</IconName>
    </IconWrapper>
  );
}

const iconNames = [
  { outlineIcon: "home-outline", fullIcon: "home", name: "Início" },
  { outlineIcon: "film-outline", fullIcon: "film", name: "Filmes" },
  {
    outlineIcon: "book-outline",
    fullIcon: "book",
    name: "Livros",
  },
  { outlineIcon: "tv-outline", fullIcon: "tv", name: "Séries" },
  { outlineIcon: "menu-outline", fullIcon: "menu", name: "Mais" },
];

export default function BottomNavigationMenu() {
  const [menuClicked, setMenuClicked] = useState("");
  return (
    <MenuWrapper>
      {iconNames.map((element) => {
        return (
          <MenuIcon
            outlineIcon={element.outlineIcon}
            fullIcon={element.fullIcon}
            name={element.name}
            menuClicked={menuClicked}
            setMenuClicked={setMenuClicked}
            key={element.name}
          />
        );
      })}
    </MenuWrapper>
  );
}

const MenuWrapper = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  display: flex;
  height: 100px;
  width: 100vw;
  background-color: magenta;
  align-items: center;
  justify-content: space-around;
`;

const IconWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: auto;
  height: 60px;

  > ion-icon {
    font-size: 30px;
    padding-bottom: 2px;
  }
`;

const IconName = styled.h1`
  font-size: 20px;
`;
